package Day_02_Printing_Comments;

public class Task4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("\"\\__/\"\n");
		System.out.println("He said \"Hello\".\n");
		System.out.println("Would you like \'coffee\' or 'tea'?\n");
		// single quote ' \ bu olmadan da calisiyor.
		
		System.out.println("\"\\__/\"\n" + "He said \"Hello\".\n" + "Would you like \'coffee\' or \'tea\'?");
	}

}
